function [EPS,R,SO,V0,V2,V4,V6] = s83tconpc(SF,OR,RAD,ER,RF,F,ESQ)

% function [EPS,R,SO,V0,V2,V4,V6] = s83tconpc(SF,OR,RAD,ER,RF,F,ESQ)
% convert state plane to geodesic co-ordinates
% original BY E. CARLSON / subroutine
% 15.03.12 M.P.

% TRANSVERSE MERCATOR PROJECTION
% PRECOMPUTATION OF CONSTANTS

% ************* SYMBOLS AND DEFINITIONS ***********************
%   ER IS THE SEMI-MAJOR AXIS FOR GRS-80
%   SF IS THE SCALE FACTOR AT THE CM
%   SO IS THE MERIDIANAL DISTANCE (TIMES THE SF) FROM THE
%         EQUATOR TO SOUTHERNMOST PARALLEL OF LAT. FOR THE ZONE
%   R IS THE RADIUS OF THE RECTIFYING SPHERE
%   U0,U2,U4,U6,V0,V2,V4,V6 ARE PRECOMPUTED CONSTANTS FOR
%     DETERMINATION OF MERIDIANAL DIST. FROM LATITUDE
%   OR IS THE SOUTHERNMOST PARALLEL OF LATITUDE FOR WHICH THE
%         NORTHING COORD IS ZERO AT THE CM
% *************************************************************

      F=1.00/298.25722210100;
      EPS=ESQ/(1.00-ESQ);
      PR=(1.00-F)*ER;
      EN=(ER-PR)/(ER+PR);
      EN2=EN*EN;
      EN3=EN*EN*EN;
      EN4=EN2*EN2;

      C2=-3.00*EN/2.00+9.00*EN3/16.00;
      C4=15.00*EN2/16.00-15.00*EN4/32.00;
      C6=-35.00*EN3/48.00;
      C8=315.00*EN4/512.00;
      U0=2.00*(C2-2.00*C4+3.00*C6-4.00*C8);
      U2=8.00*(C4-4.00*C6+10.00*C8);
      U4=32.00*(C6-6.00*C8);
      U6=128.00*C8;

      C2=3.00*EN/2.00-27.00*EN3/32.00;
      C4=21.00*EN2/16.00-55.00*EN4/32.00;
      C6=151.00*EN3/96.00;
      C8=1097.00*EN4/512.00;
      V0=2.00*(C2-2.00*C4+3.00*C6-4.00*C8);
      V2=8.00*(C4-4.00*C6+10.00*C8);
      V4=32.00*(C6-6.00*C8);
      V6=128.00*C8;

      R=ER*(1.00-EN)*(1.00-EN*EN)*(1.00+2.2500*EN*EN+(225.00/64.00)*EN4);
      COSOR=cos(OR);
      OMO=OR+sin(OR)*COSOR*(U0+U2*COSOR*COSOR+U4*COSOR^4+U6*COSOR^6);
      SO=SF*R*OMO;
